Command.CommandString:
update CMD_Cell set  CellName=@CellName,AreaCode=@AreaCode,ShelfCode=@ShelfCode,CellRow=@CellRow,
CellColumn=@CellColumn,IsActive=@IsActive,IsLock=@IsLock,Memo=@Memo
where CellCode={0}

INSERT INTO CMD_PRODUCT(ProductCode, ProductName, ProductTypeCode, TrainTypeCode, AxieNo, WheelDiameter, CCZ_Diameter, FCCZ_Diameter, CCD_Diameter,
FCCD_Diameter, CCXPBZW_Size, FCCXPBZW_Size, GearNo, CCLX_Flag, FCCLX_Flag, CCLX_Year, FCCLX_Year, CCLX_Factory, FCCLX_Factory, CX_Factory, CCLG_Flag,
FCCLG_Flag, Memo, WarehouseCode, AreaCode, Creator, CreateDate, Updater, UpdateDate, LDXC, CX_DateTime)
values(@ProductCode,@ProductName,@ProductTypeCode,@TrainTypeCode,@AxieNo,@WheelDiameter,@CCZ_Diameter,@FCCZ_Diameter,@CCD_Diameter,@FCCD_Diameter,
@CCXPBZW_Size,@FCCXPBZW_Size,@GearNo,@CCLX_Flag,@FCCLX_Flag,@CCLX_Year,@FCCLX_Year,@CCLX_Factory,@FCCLX_Factory,@CX_Factory,@CCLG_Flag,
@FCCLG_Flag,@Memo,@WarehouseCode,@AreaCode,@Creator,getDate(),@Updater, getDate(),@LDXC, @CX_DateTime)

select WAREHOUSE_CODE as id,'0' as pid,'仓库：'+WAREHOUSE_NAME as name from  CMD_WAREHOUSE where IsManage=1
union all
select AREA_CODE as id,WAREHOUSE_CODE as PID,  '库区:'+AREA_NAME as name from  CMD_WH_AREA where {0}
union all
select SHELF_CODE as id,AREA_CODE as PID,'货架:'+SHELF_NAME as name from CMD_WH_SHELF where {1}
